import paho.mqtt.client as mqttimport jsonimport timeimport osfrom PythonBackend.Functions.ReadJSON import JSONHandlerclass MQTT:    def __init__(self, device_name):        self.device_name = device_name        self.client = mqtt.Client(self.device_name)        self.topic = "/{d}/INTERN/CPU/TEMP".format(d=device_name)        self.config = JSONHandler    def connect(self):        q = self.client.connect("169.254.249.196", 1883)        return "Connection to 169.254.248.196a, Quality {i}".format(i=q)    def send(self, msg):        msg = json.dumps(msg)        q = self.client.publish(self.topic, msg)        return q    def create_msg(self):        data = self.get_data()        timestamp = time.asctime()        msg = {"time": timestamp, "data": data}        return msg    def get_data(self):        temp = os.popen("vcgencmd measure_temp").readline()        return temp.replace("temp=", "").strip()    def main(self):        try:            print(self.connect())            time.sleep(5)            while True:                self.send(self.create_msg())                print("Message SEND")                time.sleep(1)        finally:            self.client.disconnect()if __name__ == '__main__':    MQTT("RASPI").main()